mapper.insert:

	select seq_product_prod_no.NEXTVAL FROM DUAL

INSERT INTO 
product(prod_no,prod_name,PRICE,IMAGE_FILE,PROD_DETAIL,MANUFACTURE_DAY,reg_date,product_value)
VALUES(	#{prodNo}
		,#{prodName}
		,#{price}
		,null
		#{fileName}				
		,#{prodDetail}
		,#{manuDate}
		,SYSDATE
		,#{productValue})

mapper.update:
UPDATE product
prod_name = #{prodName}, prod_detail = #{prodDetail}, image_file = #{fileName}, manufacture_day = #{manuDate}, price =#{price}, reg_date = SYSDATE
WHERE prod_no=#{prodNo}
